mapper.insert:
INSERT INTO t_cost (costId, name, version, supplierId, contactName, phone, location, startDate, endDate, isContainTax, isContainFreight, supplierType, totalPrice, totalPriceTax, totalAmount, checkState)
VALUES (#{costId}, #{name}, #{version}, #{supplier.supplierId}, #{supplier.contactName}, #{supplier.phone}, #{supplier.location}, #{startDate}, #{endDate}, #{isContainTax}, #{isContainFreight}, #{supplier.supplierType}, #{totalPrice}, #{totalPriceTax}, #{totalAmount}, #{checkState});


        INSERT INTO r_costandwastes (costId, wastesId) VALUES (#{id}, #{wastes.id});
        INSERT INTO t_wastes (id, name, formType, wastesId, contractAmount, unitPriceTax, unitPrice, taxRate, ph, ashPercentage, wetPercentage, calorific, halogenPercentage, sulfurPercentage, flashPoint)
        VALUES (#{wastes.id}, #{wastes.name}, #{wastes.formType}, #{wastes.wastesId}, #{wastes.contractAmount}, #{wastes.unitPriceTax}, #{wastes.unitPrice}, #{wastes.taxRate}, #{wastes.ph},
        #{wastes.ashPercentage}, #{wastes.wetPercentage}, #{wastes.calorific}, #{wastes.halogenPercentage}, #{wastes.sulfurPercentage}, #{wastes.flashPoint});

INSERT INTO t_cost (costId, name, version, supplierId, supplierType, contactName, phone, location, startDate, endDate, isContainTax, isContainFreight, totalPriceTax, totalPrice, totalAmount, checkState)
VALUES (#{costId}, #{name}, #{version}, #{supplier.supplierId}, #{supplier.supplierType}, #{supplier.contactName}, #{supplier.phone}, #{supplier.location}, #{startDate}, #{endDate}, #{isContainTax}, #{isContainFreight}, #{totalPriceTax}, #{totalPrice}, #{totalAmount}, #{checkState});


        INSERT INTO r_costandwastes (costId, wastesId) VALUES (#{id}, #{wastes.id});
        INSERT INTO t_wastes (id, name, formType, wastesId, contractAmount, unitPriceTax, unitPrice, taxRate, ph, ashPercentage, wetPercentage, calorific, halogenPercentage, sulfurPercentage, flashPoint)
        VALUES (#{wastes.id}, #{wastes.name}, #{wastes.formType}, #{wastes.wastesId}, #{wastes.contractAmount}, #{wastes.unitPriceTax}, #{wastes.unitPrice}, #{wastes.taxRate}, #{wastes.ph},
        #{wastes.ashPercentage}, #{wastes.wetPercentage}, #{wastes.calorific}, #{wastes.halogenPercentage}, #{wastes.sulfurPercentage}, #{wastes.flashPoint});

mapper.update:
UPDATE t_cost SET supplierId=#{supplier.supplierId}, contactName=#{supplier.contactName}, phone=#{supplier.phone}, location=#{supplier.location}, startDate=#{startDate}, endDate=#{endDate},
isContainTax=#{isContainTax}, isContainFreight=#{isContainFreight}, supplierType=#{supplier.supplierType}, totalPrice=#{totalPrice}, totalPriceTax=#{totalPriceTax}, totalAmount=#{totalAmount} WHERE id=#{id};


        UPDATE t_wastes SET name=#{wastes.name}, formType=#{wastes.formType}, wastesId=#{wastes.wastesId}, contractAmount=#{wastes.contractAmount}, unitPriceTax=#{wastes.unitPriceTax},
        unitPrice=#{wastes.unitPrice}, taxRate=#{wastes.taxRate}, ph=#{wastes.ph}, ashPercentage=#{wastes.ashPercentage}, wetPercentage=#{wastes.wetPercentage}, calorific=#{wastes.calorific},
        halogenPercentage=#{wastes.halogenPercentage}, sulfurPercentage=#{wastes.sulfurPercentage}, flashPoint=#{wastes.flashPoint} WHERE id=#{wastes.id};

mapper.select:
SELECT * FROM t_wastes WHERE id in (
  SELECT r_costandwastes.wastesId FROM r_costandwastes
  WHERE costId=#{id});
