mapper.select:
select count(*) from t_pl_inboundorder
and boundType=#{boundType} and (inboundOrderId like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y-%m-%d') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y-%c-%d') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y-%c-%e') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y%m%d') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y%c%d') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y%c%e') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y.%m.%d') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y.%c.%d') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y.%c.%e') like "%"#{keyword}"%" or recordState=#{keyword} or checkState=#{keyword}) or warehouseId in (select warehouseId from t_pl_warehouse where warehouseName like "%"#{keyword}"%") or remarks like "%"#{keyword}"%"
and inboundOrderId LIKE "%"#{inboundOrderId}"%"
and (DATE_FORMAT(inboundDate,'%Y-%m-%d') >= #{companyId})
and (DATE_FORMAT(inboundDate,'%Y-%m-%d') <= #{modifierId})
and boundType=#{boundType}
and recordState = #{recordState}
and checkState = #{checkState}
and warehouseId in (select warehouseId from t_pl_warehouse where warehouseName like "%"#{wareHouse.wareHouseName}"%")

select * from t_pl_inboundorder
and boundType=#{boundType} and (inboundOrderId like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y-%m-%d') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y-%c-%d') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y-%c-%e') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y%m%d') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y%c%d') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y%c%e') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y.%m.%d') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y.%c.%d') like "%"#{keyword}"%" or DATE_FORMAT(inboundDate,'%Y.%c.%e') like "%"#{keyword}"%" or recordState=#{keyword} or checkState=#{keyword}) or remarks like "%"#{keyword}"%" or warehouseId in (select warehouseId from t_pl_warehouse where warehouseName like "%"#{keyword}"%")
and inboundOrderId LIKE "%"#{inboundOrderId}"%"
and (DATE_FORMAT(inboundDate,'%Y-%m-%d') >= #{companyId})
and (DATE_FORMAT(inboundDate,'%Y-%m-%d') <= #{modifierId})
and boundType=#{boundType}
and recordState = #{recordState}
and checkState = #{checkState}
and warehouseId in (select warehouseId from t_pl_warehouse where warehouseName like "%"#{wareHouse.wareHouseName}"%")
order by nowTime desc
limit #{page.start}, #{page.count};

