mapper.select:
select * from t_pl_procurement where procurementCategory=1 and nonMaterial=0 order by checkStateId=69,nowTime
desc

    limit #{start}, #{count}

select * from t_pl_procurement where procurementCategory=0 and nonMaterial=0 order by checkStateId=69, nowTime
desc

    limit #{start}, #{count}

select * from t_pl_procurement where procurementCategory=0 and nonMaterial=1 order by checkStateId=69, nowTime
desc

    limit #{start}, #{count}

select * from t_pl_material


        and applyMouth like "%"#{applyMouth}"%"



            and materialCategoryId =#{materialCategoryItem.dataDictionaryItemId}


    and t_pl_material.receiptNumber in(select t_pl_procurement.receiptNumber from t_pl_procurement where
    t_pl_procurement.checkStateId=73 and t_pl_procurement.procurementCategory=1) and
    (t_pl_material.checkStateId=70);

update.if:
UPDATE t_pl_procurement SET procurementFileURL=#{procurementFileURL} WHERE receiptNumber=#{receiptNumber};
INSERT INTO t_document (receiptNumber, filePath, documentType) VALUES (#{receiptNumber},
#{procurementFileURL},'Procurement');
