mapper.select:
select * from t_pr_waybill
or id like "%"#{keywords}"%" or produceCompanyName like "%"#{keywords}"%" or total like "%"#{keywords}"%" or freight like "%"#{keywords}"%" or founder like "%"#{keywords}"%" or DATE_FORMAT(wayBillDate,'%Y-%m-%d') like "%"#{keywords}"%" or DATE_FORMAT(wayBillDate,'%Y-%c-%d') like "%"#{keywords}"%" or DATE_FORMAT(wayBillDate,'%Y-%c-%e') like "%"#{keywords}"%" or DATE_FORMAT(wayBillDate,'%Y%m%d') like "%"#{keywords}"%" or DATE_FORMAT(wayBillDate,'%Y%c%d') like "%"#{keywords}"%" or DATE_FORMAT(wayBillDate,'%Y%c%e') like "%"#{keywords}"%" or DATE_FORMAT(wayBillDate,'%Y.%m.%d') like "%"#{keywords}"%" or DATE_FORMAT(wayBillDate,'%Y.%c.%d') like "%"#{keywords}"%" or DATE_FORMAT(wayBillDate,'%Y.%c.%e') like "%"#{keywords}"%" or remarks like "%"#{keywords}"%" or produceCompanyOperator like "%"#{keywords}"%" or state like "%"#{keywords}"%" or checkStateId in (select dataDictionaryItemId from datadictionaryitem where dictionaryItemName like "%"#{keywords}"%")
and id like "%"#{id}"%"
and produceCompanyName like "%"#{produceCompanyName}"%"
and total = #{total}
and freight = #{freight}
and founder like "%"#{founder}"%"
and DATE_FORMAT(wayBillDate, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')
and DATE_FORMAT(wayBillDate, '%Y-%m-%d') <= DATE_FORMAT(#{endDate}, '%Y-%m-%d')
and produceCompanyOperator like "%"#{produceCompanyOperator}"%"
and t_pr_waybill.checkStateId = #{checkStateItem.dataDictionaryItemId}
order by creationTime desc
limit #{page.start}, #{page.count}

select * from t_pr_waybillitem
where wayBillId in ( select id from t_pr_waybill where produceCompanyId=#{clientId} and state = 'Approval')
and wastesCode =#{code};

mapper.update:
update t_pr_waybillitem
set salesmanName =#{salesmanName}
where wayBillId in (select id from t_pr_waybill where produceCompanyName =#{companyName})
